import axiso from "axios";

export const getPosts = () => async(dispatch) => {
    try {
        const { data } = axios.get('/posts');
        dispatch({
            type : 'FETCH',
            payload : data
        });
    } catch (error) {
        console.log(error.message);
        process.exit(1);
    }
};
